  `author_image` varchar(100) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

--
-- Dumping data for table `poems`
--

INSERT INTO `poems` (`id`, `poem`, `author`, `author_image`) VALUES
(1, 'Akşamüstüne doğru, kış vakti\nBir hasta odasının penceresinde\nYalnız bende değil yalnızlık hali\nDeniz de karanlık, gökyüzü de\nBir acaip, kuşların hali.', 'Orhan Veli Kanık', 'img/Orhan Veli Kanık/orhan-veli-as.jpg'),
(2, 'Ne hoş, ey güzel Tanrım, ne hoş\nMagillerde sefer etmek!\nBir sahilden çözülüp gitmek\nDüşünceler gibi başıboş.', 'Orhan Veli Kanık', 'img/Orhan Veli Kanık/orhan-veli-as.jpg'),
(3, 'Bekliyorum\r\nÖyle bir havada gel ki,\r\nvazgeçmek mümkün olmasın!', 'Orhan Veli Kanık', 'img/Orhan Veli Kanık/orhan-veli-as.jpg'),
(4, 'Bakakalırım giden geminin ardından;\r\nAtamam kendimi denize, dünya güzel;\r\nSerde erkeklik var, ağlayamam.', 'Orhan Veli Kanık', 'img/Orhan Veli Kanık/orhan-veli-as.jpg'),
(5, 'Her gün bu kadar güzel mi bu deniz?\nBöyle mi görünür gökyüzü her zaman?\nHer zaman güzel mi bu kadar,\nBu eşya, bu pencere?\nDeğil,\nVallahi değil;\nBir iş var bu işin içinde. ', 'Orhan Veli Kanık', 'img/Orhan Veli Kanık/orhan-veli-as.jpg'),
(6, 'Her gün bu kadar güzel mi bu deniz?\r\nBöyle mi görünür gökyüzü her zaman?\r\nHer zaman güzel mi bu kadar,\r\nBu eşya, bu pencere?\r\nDeğil,\r\nVallahi değil;\r\nBir iş var bu işin içinde. ', 'Orhan Veli Kanık', 'img/Orhan Veli Kanık/orhan-veli-as.jpg');

